'use strict';

var _ = require('../');

test('script line shape', function () {
  expect(new _.ScriptLine(5, 'foobar', true)).toEqual({
    lineNumber: 5,
    content: 'foobar',
    highlight: true
  });
});

test('script line to provide default highlight', function () {
  expect(new _.ScriptLine(5, 'foobar')).toEqual({
    lineNumber: 5,
    content: 'foobar',
    highlight: false
  });
});